          // LoanServices
          loan_schemes_title: 'Loan Schemes',
          loan_schemes_description: 'Being a Commercial Bank, giving Loans and Advances is among our primary activities. Our Bank offers various loans to finance seekers. We have many types of loan products specially designed to suit your personal needs.',
          common_rules_title: 'Common Rules for Becoming a Loan Holder',
          our_loan_types_title: 'Our Loan Types',
          common_rule_1: 'Applicant must be a Share Holder (Share Holder or Nominal Member).',
          common_rule_2: 'Applicant should complete the application neatly for the required loan type.',
